import React, {Component} from 'react';
import {cards} from "../utils/Constants";
let gameCard = cards;

class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            player : props.player,
            hand1 : '',
            hand2 : '',
            countWin: 0,
            countLose: 0,
            isWin: false
        }
    }

    // gameProcess = (hand1,hand2) => {
    //     // this.setState({hand1 : gameCard.shift()})
    //     // this.setState({hand2 : gameCard.shift()})
    // }


    render() {
        return (
            <div>
                    <h2>computer</h2>
                    <section>
                        <h3>{this.state.hand1}</h3>
                        <h3>{this.state.hand2}</h3>
                    </section>
                    <button>next</button>
                    <h2>{this.state.player}</h2>
            </div>

        );
    };
}

export default Game;
