import React from 'react';
import {gamePage, startPage} from "../utils/Constants";


const Start = props => {
    const handleStartGame = event => {
        const player = event.currentTarget.player.value.trim()
        if(player ==='') {
            alert('Enter your name')
            props.changePage(startPage, player);
        } else {
            props.gamePage(startPage, player);
        }
    }
    return (
        <div>
            <form onSubmit={handleStartGame}>
                <h1>Ready for War</h1>
                <input type='text' placeholder='enter name' autoFocus={true} name='player'/>
                <button type='submit'>start</button>
            </form>
        </div>
    );
};

export default Start;
