import React, {Component} from 'react';
import {cards} from "../utils/Constants";
let gameCard = cards;

class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            player : props.player,
            hand1 : '',
            hand2 : '',
            countWin: 0,
            countLose: 0,
            isWin: false
        }
    }

    gameProcess = () => {
        this.hand1 = gameCard.shift();
        this.hand2 = gameCard.shift();
    }


    render() {
        return (
            <div>
                <form>
                    <h2>computer</h2>
                    <section>
                        <h3>{this.state.hand1}</h3>
                        <h3>{this.state.hand2}</h3>
                    </section>
                    <button onClick={this.gameProcess}>next</button>
                    <h2>{this.state.player}</h2>
                </form>
            </div>

        );
    };
}

export default Game;
