import React, {Component} from 'react';
import {cards, finishPage, gamePage} from "../utils/Constants";

let gameCard = [...cards];

function start (){
    gameCard = [...cards]
    gameCard.sort(() => Math.random() - 0.5);
}

class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            player: props.player,
            hand1: '',
            hand2: '',
            game : {
                countLose : props.game.countLose,
                countWin: props.game.countWin,
                isWin: props.game.isWin
            }


        }
    }

    gameProcess = props => {
        if(gameCard.length === 0){
            start();
            this.state.game.isWin = this.state.game.countLose < this.state.game.countWin;
            this.props.changePage(finishPage,this.state.game, this.state.player);
        } else {
            let hand1 = gameCard.shift();
            this.setState({hand1: hand1});
            let hand2 = gameCard.shift()
            this.setState({hand2: hand2});
            hand1 > hand2 ? this.state.game.countLose++ : this.state.game.countWin++;

        }
    }


    render() {
        return (
            <div>
                <h2>computer</h2>
                <section>
                    <h3>{this.state.hand1}</h3>
                    <h3>{this.state.hand2}</h3>
                </section>
                <button onClick={this.gameProcess}>next</button>
                <h2>{this.state.player}</h2>
            </div>

        );
    };
}

export default Game;
